/* Este test comprueba la navegacion en la página haciendo coincidir
* la Url que tenemos en el momento que pulsamos un boton del menu de navegacion o
* un enlace, con la Url que debería de mostrar, comprobando así que la ruta es la correcta.
*/
var webdriver = require('selenium-webdriver'),
By = webdriver.By,
until = webdriver.until;

var driver = new webdriver.Builder()
.forBrowser('chrome')
.build();

driver.get('http://localhost:8080/mayia.html#!/'); //URL principal

driver.findElement(By.name('cursos')).click()
.then(
	driver.wait(until.urlIs('http://localhost:8080/mayia.html#!/cursos')),2000)
	.then(
		console.log('URL Correcta'), err=>console.log('URL Incorrecta'));

driver.findElement(By.name('perfil')).click()
.then(
	driver.wait(until.urlIs('http://localhost:8080/mayia.html#!/perfil')),2000)
	.then(
		console.log('URL Correcta'), err=>console.log('URL Incorrecta'));

